import React, { useEffect } from 'react'
import { useState, useCallback } from 'react';
import {
  Page,
  Grid,
  Card,
  TextField,
  Select,
  ResourceList,
  Filters,


} from '@shopify/polaris';


function Home() {
  const [queryValue, setQueryValue] = useState(null);
  const handleFiltersQueryChange = useCallback(
    (value) => setQueryValue(value),
    [],
  );


  const [textFieldValue, setTextFieldValue] = useState('');
  const handleTextFieldChange = useCallback(
    (value) => setTextFieldValue(value), [],
  );
  // input field state
  const handleClearButtonClick = useCallback(() => setTextFieldValue(''), []);
  const [checked, setChecked] = useState(false);
  const handleChange = useCallback((newChecked) => setChecked(newChecked), []);

  // Select Option State
  const [selected1, setSelected1] = useState('username');
  const [selected2, setSelected2] = useState('contains');
  const handleSelectChange1 = useCallback((value) => setSelected1(value), []);
  const handleSelectChange2 = useCallback((value) => setSelected2(value), []);
  // Select Options Values Asigns
  const condOpt1 = [
    { label: 'User Name', value: 'username' },
    { label: 'Id', value: 'id' },
    { label: 'Followers', value: 'followers' },
  ];
  const condOpt2 = [
    { label: 'Contains', value: 'contains' },
    { label: 'Does! Not Contains', value: 'doesnotcontains' },
    { label: 'Equals To', value: 'equalsto' },
    { label: 'Not Equals To', value: 'notequalsto' },
  ];

  // Codition Logic 
  const [rowdata, setRowData] = useState([]);
  console.log(rowdata, "row");
  const allGitData = []

  useEffect(() => {
    if (selected1 == 'username' && selected2 == 'contains') {
      gitHubData();
    }
  });
  async function gitHubData() {
    const tmp = [];
    const gitUser = await fetch('https://api.github.com/users');
    const allgitUser = await gitUser.json();
    allgitUser.forEach(user => {
      console.log(textFieldValue, "sfasdfas");
      if (textFieldValue == user.login) {
        tmp.push({
          id: user.id,
          name: user.login,
          userPic: user.avatar_url,
          userUrl: user.html_url,
          userType: user.type,
        });
      }
    });
    allGitData.push(allgitUser)
    setRowData(tmp);
  }

  return (
    <>
      <Page>
        <Card sectioned>
          <Grid>
            <Grid.Cell columnSpan={{ xs: 6, sm: 3, md: 3, lg: 8, xl: 6 }}>
              <TextField
                label={"By " + selected1 + " or " + selected2}
                value={textFieldValue}
                onChange={handleTextFieldChange}
                clearButton
                onClearButtonClick={handleClearButtonClick}
                autoComplete="off"
              />
            </Grid.Cell>
            <Grid.Cell columnSpan={{ xs: 6, sm: 3, md: 3, lg: 2, xl: 6 }}>
              <Select
                label="Filter"
                options={condOpt1}
                onChange={handleSelectChange1}
                value={selected1}
              />
            </Grid.Cell>
            <Grid.Cell columnSpan={{ xs: 6, sm: 3, md: 3, lg: 2, xl: 6 }}>
              <Select
                label="Filter"
                options={condOpt2}
                onChange={handleSelectChange2}
                value={selected2}
              />
            </Grid.Cell>
          </Grid>
          {/* Table */}
          <ResourceList
            resourceName={{ singular: 'customer', plural: 'customers' }}
            // filterControl={
            //   <Filters
            //     queryValue={queryValue}
            //     filters={filters}
            //     appliedFilters={appliedFilters}
            //     onQueryChange={handleFiltersQueryChange}
            //     onQueryClear={handleQueryValueRemove}
            //     onClearAll={handleFiltersClearAll}
            //   />
            // }

            items={rowdata}
            // {[
            //     {
            //         id: 341,
            //         url: 'customers/341',
            //         name: 'Mae Jemison',
            //         location: 'Decatur, USA',
            //     },
            //     {
            //         id: 256,
            //         url: 'customers/256',
            //         name: 'Ellen Ochoa',
            //         location: 'Los Angeles, USA',
            //     },
            // ]}
            renderItem={(item) => {
              const { id, name, userPic, userUrl, userType } = item;
              const media = <Avatar customer size="medium" source={userPic} name={name} />;
              return (
                <ResourceList.Item
                  id={id}
                  user={userPic}
                  // url={userUrl}
                  media={media}
                  accessibilityLabel={`View details for ${name}`}
                  onClick={handleChange}
                >
                  <h3>
                    <TextStyle username={name} variation="strong">{name}</TextStyle>
                    <span>
                      <Stack>
                        <Badge>{userType}</Badge>
                      </Stack>
                    </span>
                    <span>{userUrl}</span>
                  </h3>
                  <div style={{ height: '50px' }}>
                    <Modal
                      activator={Filters}
                      open={active}
                      onClose={handleChange}
                      title="Received user via github."
                    >
                      <Modal.Section>
                        <TextContainer>
                          <Grid>
                            <Grid.Cell columnSpan={{ xs: 6, sm: 3, md: 3, lg: 4, xl: 6 }}>
                              <div sectioned>
                                <img src={userPic} style={{ height: "150px", borderRadius: "50%" }} name={name} />
                              </div>
                            </Grid.Cell>
                            <Grid.Cell columnSpan={{ xs: 6, sm: 3, md: 3, lg: 4, xl: 6 }}>
                              <div title="Profile" sectioned>
                                <div style={{ marginTop: "20px" }}>
                                  <div style={{ fontSize: "25px" }}>ID
                                    <span style={{ fontSize: "25px", marginLeft: "12px" }}><b>{id}</b></span>
                                  </div>
                                  <div style={{ marginTop: "13px" }}>
                                    <span style={{ fontSize: "25px" }}>{userType}</span>
                                    <span style={{ fontSize: "25px", marginLeft: "12px" }}><b>{name}</b></span>
                                  </div>
                                  <div style={{ marginTop: "16px" }}>
                                    <Button size="slim" url={userUrl}>View Profile</Button>
                                  </div>
                                </div>
                              </div>
                            </Grid.Cell>
                          </Grid>
                          <p>
                            Use GitHub posts to share your products with millions of
                            people. Let shoppers buy from your store without leaving
                            Github.
                          </p>
                        </TextContainer>
                      </Modal.Section>
                    </Modal>
                  </div>

                </ResourceList.Item>

              );
            }}
          />
        </Card>
      </Page>
    </>
  );
}


export default Home
