import React, { useEffect } from 'react'
import { useState, useCallback } from 'react';
import {
  Page,
  Grid,
  Card,
  TextField,
  Select,

} from '@shopify/polaris';


function Home() {
  const [textFieldValue, setTextFieldValue] = useState('');
  const handleTextFieldChange = useCallback(
    (value) => setTextFieldValue(value), [],
  );
  // input field state
  const handleClearButtonClick = useCallback(() => setTextFieldValue(''), []);
  const [checked, setChecked] = useState(false);
  const handleChange = useCallback((newChecked) => setChecked(newChecked), []);

  // Select Option State
  const [selected1, setSelected1] = useState('username');
  const [selected2, setSelected2] = useState('contains');
  const handleSelectChange1 = useCallback((value) => setSelected1(value), []);
  const handleSelectChange2 = useCallback((value) => setSelected2(value), []);
  // Select Options Values Asigns
  const condOpt1 = [
    { label: 'User Name', value: 'username' },
    { label: 'Id', value: 'id' },
    { label: 'Followers', value: 'followers' },
  ];
  const condOpt2 = [
    { label: 'Contains', value: 'contains' },
    { label: 'Does! Not Contains', value: 'doesnotcontains' },
    { label: 'Equals To', value: 'equalsto' },
    { label: 'Not Equals To', value: 'notequalsto' },
  ];

  // Codition Logic 
  const [rowdata, setRowData] = useState([]);
  console.log(rowdata, "row");
  const allGitData = []

  useEffect(() => {
    if (selected1 == 'username' && selected2 == 'contains') {
      gitHubData();
    }
  }, []);

  async function gitHubData() {
    const tmp = [];
    const gitUser = await fetch('https://api.github.com/users');
    const allgitUser = await gitUser.json();
    console.log(textFieldValue,"sfasdfas");
    allgitUser.forEach(user => {

      if (textFieldValue == user.login) {
        tmp.push({
          id: user.id,
          name: user.login,
          userPic: user.avatar_url,
          userUrl: user.html_url,
          userType: user.type,
        });
      }
    });

    allGitData.push(allgitUser)
    setRowData(tmp);


  }

  return (
    <>
      <Page>
        <Card sectioned>
          <Grid>
            <Grid.Cell columnSpan={{ xs: 6, sm: 3, md: 3, lg: 8, xl: 6 }}>
              <TextField
                label={"By " + selected1 + " or " + selected2}
                value={textFieldValue}
                onChange={handleTextFieldChange}
                clearButton
                onClearButtonClick={handleClearButtonClick}
                autoComplete="off"
              />
            </Grid.Cell>
            <Grid.Cell columnSpan={{ xs: 6, sm: 3, md: 3, lg: 2, xl: 6 }}>
              <Select
                label="Filter"
                options={condOpt1}
                onChange={handleSelectChange1}
                value={selected1}
              />
            </Grid.Cell>
            <Grid.Cell columnSpan={{ xs: 6, sm: 3, md: 3, lg: 2, xl: 6 }}>
              <Select
                label="Filter"
                options={condOpt2}
                onChange={handleSelectChange2}
                value={selected2}
              />
            </Grid.Cell>
          </Grid>
        </Card>
      </Page>
    </>
  );
}


export default Home
